// set up a three js scene in VR Mode
let ThreeJm = new threejm(settings);

// set background
ThreeJm.setBackground(settings.assets.environment.backgroundImage);

// create floor
ThreeJm.createFloor(settings.assets.environment.floor);

// create light source
ThreeJm.createLight(settings.assets.lighting.mainLight);

// create shapes
ThreeJm.createShape('lookable', settings.assets.objects.poly.lookable);
ThreeJm.createShape('photoSphere', settings.assets.objects.photoSphere.photoSphereOne);


// ThreeJm.createShape('space', assets.objects.space);
ThreeJm.createShape('videoSphere', settings.assets.objects.videoSphere.videoSphereOne);
// ThreeJm.createShape('sailingScrollSphere', assets.objects.sailingScrollSphere);

// load model
ThreeJm.loadModel('testModels', settings.assets.models.example);

